PHP Code
$dataDir = &lsquo;';
// Create Aspose.Cells Helper Object
$ptr = new \COM(&lsquo;Aspose.Cells.Interop.InteropHelper&rsquo;);
// Opening through Path
// Creating a Workbook object and opening an Excel file using its file path
$workbook = $ptr-&gt;New(&ldquo;Aspose.Cells.Workbook&rdquo;,array());
$worksheets = $ptr-&gt;Get($workbook,&ldquo;Worksheets&rdquo;,array());
$sheetIndex = $ptr-&gt;Call($worksheets,&ldquo;Add&rdquo;,array());
$worksheet = $ptr-&gt;Get($worksheets,&ldquo;Item&rdquo;,array($sheetIndex));
$cells = $ptr-&gt;Get($worksheet,&ldquo;Cells&rdquo;,array());
$a1 = $ptr-&gt;Get($cells,&ldquo;Item_3&rdquo;,array(&ldquo;A1&rdquo;));
$ptr-&gt;Call($a1,&ldquo;PutValue&rdquo;,array(1));
$a2 = $ptr-&gt;Get($cells,&ldquo;Item_3&rdquo;,array(&ldquo;A2&rdquo;));
$ptr-&gt;Call($a2,&ldquo;PutValue&rdquo;,array(2));
$a3 = $ptr-&gt;Get($cells,&ldquo;Item_3&rdquo;,array(&ldquo;A3&rdquo;));
$ptr-&gt;Call($a3,&ldquo;PutValue&rdquo;,array(3));
$a4 = $ptr-&gt;Get($cells,&ldquo;Item_3&rdquo;,array(&ldquo;A4&rdquo;));
$ptr-&gt;Call($a3,&ldquo;PutValue&rdquo;,array(3));
$ptr-&gt;Call($workbook,&ldquo;Save&rdquo;,array($dataDir.&quot;/output.xls&rdquo;));
$ptr-&gt;Set($a4,&ldquo;Formula&rdquo;,&ldquo;=SUM(A1:A3)&quot;,array());
$ptr-&gt;Call($workbook,&ldquo;CalculateFormula&rdquo;,array());
